INSERT INTO "enfermedad" VALUES (69, 'Cenicilla polvorienta', 'Sphaerotheca fuliginea', 'Es una enfermedad ampliamente distribuida donde se cultivan cucurbitáceas. Inicialmente se observan en el envés de las hojas manchas cloróticas muy tenues y posteriormente colonias de aspecto polvoso (conidias y conidióforos). Las estructuras pueden cubrir haz y envés, extendiéndose a pecíolos y tallos. Las hojas infectadas severamente se tornan amarillentas al avanzar la enfermedad y sobreviene la defoliación.', ' ', ' ', ' ', 0, 1, 78);
INSERT INTO "enfermedad" VALUES (71, 'Mancha de Cercospora en esparrago', ' ', ' ', ' ', ' ', ' ', 0, 1, 36);
