INSERT INTO "enfermedad" VALUES (143, 'Minador de la Hoja', 'Liriomyza sp y Liriomyza trifolii', 'Insecto con apariencia de mosca pequeña, la hembra es de tamaño mayor que el macho, su ciclo pasa por los estados de huevo, 3 fases larvarias, pupa y adulto, su ciclo de vida dura 25 días. Los adultos realizan picaduras en las hojas para alimentarse y las larvas realizan galerias en las hojas que posteriormente se necrosan, reduciendo la capacidad fotosintetica de la planta.', null, null, null, 0, 16, 17);
