INSERT INTO "enfermedad" VALUES (159, 'Mosquita Blanca', 'Bemisia tabaci', 'Los adultos se pueden localizar en todas las partes de la planta y pasan la mayor parte del tiempo alimentándose, apareándose y poniendo huevos en el envés de las hojas. Tiene tres estadios ninfales. Los daños en planta son moteado clorótico, manchas amarillentas, enrollamiento de las hojas y de los tallos, retraso en el crecimiento de plantas, marchitamiento y pérdida de hojas.', null, null, null, 0, 29, 18);
