INSERT INTO "enfermedad" VALUES (256, 'Gusano Bellotero', 'Heliothis spp', 'Pasa por los estados de huevecillo que dura de 2 a 5 días, seguido por larvas que dura 14 a 30 días, pasando por 5 a 6 estadios y alcanzan de 4 a 5 cm, las pupas duran 10 a 18 días y están presentes en el suelo, las palomillas miden de 2.5 a 3.5 cm, su color es pardo, las hembras pueden llegar a colocar 400 huevecillos. Las larvas se alimentan de los botones y bellotas, perforan y consumen todo su interior que contribuye a la perdida temprana de estructuras productivas.', null, null, null, 0, 22, 4);
INSERT INTO "enfermedad" VALUES (257, 'Gusano Bellotero', 'Heliothis spp', 'Pasa por los estados de huevecillo que dura de 2 a 5 días, seguido por larvas que dura 14 a 30 días, pasando por 5 a 6 estadios y alcanzan de 4 a 5 cm, las pupas duran 10 a 18 días y están presentes en el suelo, las palomillas miden de 2.5 a 3.5 cm, su color es pardo, las hembras pueden llegar a colocar 400 huevecillos. Las larvas se alimentan de los botones y bellotas, perforan y consumen todo su interior que contribuye a la perdida temprana de estructuras productivas.', null, null, null, 0, 22, 20);
INSERT INTO "enfermedad" VALUES (258, 'Gusano Trozador', 'Agrotis spp', 'El adulto es de color pardo amarillento con manchas más oscuras, huevecillo de color blanco grisáceo con longitud de 0.5 mm, la larva es de color gris oscuro, su longitud va de 40 a 50 mm, la pupa de color marrón rojizo, con longitud de 20 a 30 mm. Es de hábito nocturno, alimentándose principalmente de tallos tiernos, cortando, trozando y ocasionando el deterioro de la planta.', null, null, null, 0, 22, 4);
