INSERT INTO "enfermedad" VALUES (286, 'Gusano Perro', 'Papilio cresphontes', 'Gusano, oruga o larva de considerable tamaño, de color carmelita oscuro con manchas amarillas, las larvas son moteadas de gris y carmelita, la cabeza en la zona ensanchada de su cuerpo, adopta la forma de la cabeza de un perro, en estado adulto es una mariposa amarilla y negra con una cola semejante a las de las golondrinas. En estado larval se alimentan de los renuevos de los cítricos y puede quitarle todas las hojas. ', null, null, null, 0, 22, 55);
