INSERT INTO "enfermedad" VALUES (305, 'Barrenador del Fruto', 'Diaphania spp', 'Los huevecillos son aplanados, de color blanco cremoso hasta rojizo, las larvas pasan 5 estadios que duran de 14 a 21 días, la pupa es envuelta en un capullo cerca de las plantas hospederas y dura de 5 a 10 días. Las larvas se alimentan del follaje y producen galerías en los tallos, causando la muerte de las guías y caída prematura de los frutos. En los frutos infestados baja la calidad y el rendimiento.', null, null, null, 0, 22, 17);
